import axios from 'axios';
import * as api from '../api/index.js';

export const getPosts = () => async (dispatch) => {
  try {
    const  data  = await api.fetchPosts();
    const obj= await axios.get("https://gorest.co.in/public-api/users");
    console.log(obj.data.data);
    dispatch({ type: "FETCH", payload: obj.data.data });
    await api.createPosts(obj.data.data)

  } catch (error) {
    console.log(error.message);
  }
};

export const updatePost = (id, user) => async (dispatch) => {
  try {
    const { data } = await api.updatePost(id, user);

    dispatch({ type: "UPDATE", payload: data });
  } catch (error) {
    console.log(error.message);
  }
};
